// Parse AI response to JSON
const aiResponse = $input.first().json.text || $input.first().json.output;

try {
  // Extract JSON from response
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);

    // Map English categories to Indonesian category IDs
    const categoryMap = {
      'food': 'food',
      'transport': 'transport',
      'shopping': 'shopping',
      'entertainment': 'entertainment',
      'health': 'health',
      'bills': 'bills',
      'education': 'education',
      'salary': 'salary',
      'bonus': 'bonus',
      'investment': 'investment',
      'freelance': 'freelance',
      'other': 'other'
    };

    return {
      json: {
        type: parsed.type,
        amount: parseInt(parsed.amount),
        categoryId: categoryMap[parsed.category] || 'other',
        description: parsed.description,
        date: new Date().toISOString().split('T')[0],
        source: 'telegram',
        chatId: $('Telegram Trigger').first().json.message.chat.id,
        originalMessage: $('Telegram Trigger').first().json.message.text
      }
    };
  }
} catch (e) {
  return {
    json: {
      error: 'Failed to parse: ' + e.message,
      originalMessage: $('Telegram Trigger').first().json.message.text
    }
  };
}

return { json: { error: 'No valid JSON found' } };
